"use client";

import {
  Breadcrumb,
  BreadcrumbItem,
  BreadcrumbSeparator,
} from "@camped-ui/breadcrumb";
import {
  Select,
  SelectContent,
  SelectItem,
  SelectTrigger,
  SelectValue,
} from "@camped-ui/select";
import Link from "next/link";
import { useState } from "react";

const OPTIONS = ["ghost", "outline", "link", "default", "destructive"];

const BreadCrumbVariantPicker = ({
  variant,
  setVariant,
}: {
  variant: string;
  setVariant: (variant: string) => void;
}) => {
  return (
    <div className="absolute right-2 top-2">
      <Select defaultValue="ghost" value={variant} onValueChange={setVariant}>
        <SelectTrigger className="px-2 h-8 bg-background text-xs w-40">
          <SelectValue>
            <span className="text-muted-foreground font-bold">Variant : </span>
            <span>{variant}</span>
          </SelectValue>
        </SelectTrigger>
        <SelectContent align="end">
          {OPTIONS.map((opt, index) => (
            <SelectItem key={`${opt}-${index}`} value={opt} className="text-sm">
              {opt}
            </SelectItem>
          ))}
        </SelectContent>
      </Select>
    </div>
  );
};

const BreadCrumbTest = () => {
  const [variant, setVariant] = useState("ghost");
  return (
    <>
      <Breadcrumb
        orientation="horizontal"
        variant={variant as any}
        className="gap-1 bg-background rounded-lg p-2"
      >
        <BreadcrumbItem className="px-2 h-7" index={0}>
          <Link href="/">Home</Link>
        </BreadcrumbItem>
        <BreadcrumbSeparator />
        <BreadcrumbItem className="px-2 h-7" index={1}>
          Settings
        </BreadcrumbItem>
        <BreadcrumbSeparator />
        <BreadcrumbItem className="px-2 h-7" index={2}>
          Account
        </BreadcrumbItem>
      </Breadcrumb>
      <BreadCrumbVariantPicker variant={variant} setVariant={setVariant} />
    </>
  );
};

export BreadCrumbTest;

import {
  Breadcrumb,
  BreadcrumbItem,
  BreadcrumbSeparator,
} from "@camped-ui/breadcrumb";
import Link from "next/link";

const Pages = [
  {
    title: "Home",
    path: "/",
  },
  {
    title: "Components",
    path: "/docs/components",
  },
  {
    title: "Breadcrumb",
    path: "#",
  },
];

const BreadcrumbOrientation = () => {
  return (
    <Breadcrumb
      orientation="vertical"
      variant={"ghost"}
      className="gap-1 bg-background rounded-lg p-2"
    >
      {Pages.map((page, index) => {
        return (
          <div
            key={`${page.title}-path`}
            className="flex flex-col items-center gap-1"
