import { InputOTP } from "@camped-ui/input-otp";
import React, { useEffect, useRef } from "react";

export function AutoFocusOTP() {
  const ref = useRef(null);
  useEffect(() => {
    if (ref.current) ref.current.focusInput(0);
  }, []);

  return <InputOTP length={6} enableAutoFocus ref={ref} />;
}

import { PasswordInput } from "@camped-ui/password-input";
import React, { useState } from "react";

export function StrengthPasswordInput() {
  const [value, setValue] = useState("");
  const strength = Math.min((value.length / 12) * 100, 100);

  return (
    <div>
      <PasswordInput placeholder="Enter password" value={value} onChange={(e) => setValue(e.target.value)} />
      <div className="h-2 bg-gray-200 rounded mt-2">
        <div className="h-full bg-green-500 rounded" style={{ width: `${strength}%` }} />
      </div>
    </div>
  );
}

import { MultiSelector, MultiSelectorTrigger, MultiSelectorContent, MultiSelectorItem, MultiSelectorInput } from "@camped-ui/multi-select";
import { Avatar, AvatarImage, AvatarFallback } from "@camped-ui/avatar";
import React, { useState } from "react";

const users = [
  { name: "Alice", picture: "/users/alice.jpg" },
  { name: "Bob", picture: "/users/bob.jpg" },
  { name: "Charlie", picture: "/users/charlie.jpg" }
];

export function UserTagSelector() {
  const [selected, setSelected] = useState<string[]>(["Alice"]);
  return (
    <MultiSelector values={selected} onValuesChange={setSelected}>
      <MultiSelectorTrigger>
        <MultiSelectorInput placeholder="Select users" />
      </MultiSelectorTrigger>
      <MultiSelectorContent>
        {users.map((user) => (
          <MultiSelectorItem key={user.name} value={user.name}>
            <div className="flex items-center space-x-2">
              <Avatar>
                <AvatarImage src={user.picture} alt={user.name} />
                <AvatarFallback>{user.name.charAt(0)}</AvatarFallback>
              </Avatar>
              <span>{user.name}</span>
            </div>
          </MultiSelectorItem>
        ))}
      </MultiSelectorContent>
    </MultiSelector>
  );
}

import { Select, SelectTrigger, SelectContent, SelectItem, SelectValue, SelectInput } from "@camped-ui/select";
import React, { useState } from "react";

const seasons = ["Spring", "Summer", "Autumn", "Winter"];

export function SearchableSeasonSelect() {
  const [value, setValue] = useState("");
  const [filter, setFilter] = useState("");
  return (
    <Select onValueChange={setValue} value={value} searchable filterValue={filter} onFilterChange={setFilter}>
      <SelectTrigger>
        <SelectValue placeholder="Select a season" />
      </SelectTrigger>
      <SelectContent>
        <SelectInput placeholder="Search..." />
        {seasons
          .filter((s) => s.toLowerCase().includes(filter.toLowerCase()))
          .map((season) => (
            <SelectItem key={season} value={season}>{season}</SelectItem>
          ))}
      </SelectContent>
    </Select>
  );
}

