import { DatePicker, DatePickerInput } from "@camped-ui/date-picker";
import { format } from "date-fns";
import React, { useState } from "react";

export function FormattedDatePicker() {
  const [date, setDate] = useState<Date | null>(null);
  return (
    <DatePicker
      value={date}
      onChange={setDate}
      parseFormat="MM/dd/yyyy"
      formatDate={(d) => (d ? format(d, "MM/dd/yyyy") : "")}
    >
      <DatePickerInput placeholder="MM/DD/YYYY" />
    </DatePicker>
  );
}

