          <div key={`${page.title}-path`} className="flex items-center gap-1">
            <BreadcrumbItem
              isActive={isActive}
              index={index}
              className="h-8 px-2 "
              activeVariant={{
                variant: "default",
              }}
            >
              <Link scroll={false} href={`?path=${page.path}`}>
                {page.title}
              </Link>
            </BreadcrumbItem>
            {index !== Pages.length - 1 && <BreadcrumbSeparator />}
          </div>
        );
      })}
    </Breadcrumb>
  );
};

